import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';

// import PersonImage from './PersonImage';


const Accordion = ({ person }) => {

  const [isOpen, setIsOpen] = useState(false);


  const toggleAccordion = () => {

    setIsOpen(!isOpen);

  };


  return (
    <div classname="accordion">
        <div classname="accordion-header" onChange={toggleAccordion}>
            <Avatar src={person.avatar} alt={person.name} />
            <div classname="person-info">
                <h2>{person.name}</h2>

              <p>{person.designation}</p>

          <p>{person.experience} years of experience</p>

          <p>{person.location}</p>

          <p>Salary: {person.salary}</p>

          <div className="rating">

            {/* Render 5 stars based on rating */}

            {Array.from({ length: 5 }, (_, index) => (

              <span key={index} className={`star ${index < person.rating ? 'filled' : ''}`}>

                &#9733;

              </span>

            ))}

          </div>

        </div>

        <button className={`accordion-toggle ${isOpen ? 'open' : ''}`}>

          View Profile

        </button>

      </div>

      {isOpen && (

        <div className="accordion-content">

          {/* Content of the accordion */}

          {/* Add additional details or components here */}

          <div className="next-page-sidebar">

            {/* Sidebar for the next page */}

            {/* Add content or components for the sidebar of the next page */}

          </div>

        </div>

      )}

    </div>

  );

};


export default Accordion;
