import * as React from 'react';
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import DeleteIcon from '@mui/icons-material/Delete';
import { styled } from '@mui/material/styles';
import DownloadIcon from '@mui/icons-material/Download';
import Icon from '@mui/material/Icon';
// import Divider from '@mui/material/Divider';
// import ListItem from '@mui/material/ListItem';
import "./MoAttachment.css";

export const DeleteButton = styled(Button)(() => ({
    color: "blue",
    backgroundColor: "#1b7028",
    cursor: "pointer",
    textTransform: "none"

}));
export const MyCardHeading = styled(Typography)((props) => ({
    color: props.color,
    fontSize: "20px",
    fontWeight: 'bold',
    marginBottom: '2px'

}));
export const MyCardSubHeading = styled(Typography)((props) => {
    console.log(props.color); return ({
        color: props.color,
        fontSize: "15px",
        fontWeight: 'bold',
        marginBottom: '2px'
    })
});
export default function SimpleAccordion() {

    const data = [{ name: "hello", desc: "esdtugugutdtufufdhgjgcgfyu" },
    { name: "hello", desc: "not hello Accordion. API. API reference docs for the React Accordion component. Learn about the props, CSS, and other APIs of this exported module API. API reference docs for the React  component. Learn about the props, CSS, and other APIs of this exported module.." }];

    const milestoneName="Milestone 1 Attachment"

    return (
        <div>
            <Accordion>
            <AccordionSummary
                    expandIcon={<ExpandMoreIcon />}
                    aria-controls="panel1a-content"
                    id="panel1a-header"
                >
                    <Typography>{milestoneName}</Typography>

                </AccordionSummary>
                <hr />
                { data.map((d)=>(
                <AccordionDetails className='attachment-detail'>
                    <div className='attachment-detail-content'>
                        <MyCardSubHeading>{d.name}</MyCardSubHeading>
                        <p>{d.desc}</p>
                    </div>
                    <div className='attachment-detail-action'>
                        <Button variant="outlined" startIcon={<DeleteIcon />}>
                            Delete
                        </Button>
                        < DownloadIcon />
                    </div>


                </AccordionDetails>
                ))}
                <Button className='add-attachment-button' variant="contained" >
                            Add Attachments
                        </Button>
            </Accordion>


        </div>
    );
}
