import React, { useState, useEffect } from "react";
import MoFilter from "./MoFilter.css";
import axios from "axios";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import SearchIcon from '@mui/icons-material/Search';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import NativeSelect from '@mui/material/NativeSelect';


function Filter() {
    let dataCheck = [
      { txID: "234", currencyType: "PHP", feeAmount: "300", status:"completed" },
      { txID: "4474", currencyType: "INR", feeAmount: "200", status:"Not Completed" },
      { txID: "37483", currencyType: "USD", feeAmount: "600", status:"completed" },
      { txID: "869", currencyType: "PHP", feeAmount: "1700", status:"Not Completed" },
      { txID: "37584", currencyType: "INR", feeAmount: "80", status:"completed" },
      { txID: "637", currencyType: "USD", feeAmount: "5000", status:"Not Completed" }
    ];

    const [searchValue, setSearchValue] = useState();
    const [newSearchedRows, setNewSearchedRows] = useState(dataCheck);

const handleSearch = (prop) => {
    const filteredRows = dataCheck.filter((row) => {
      // console.log(row);
      return row.currencyType.toLowerCase().includes(prop.toLowerCase()) || row.txID.includes(prop) || row.feeAmount.includes(prop);
      // || row.txID.includes(prop) 
    });
    console.log(filteredRows);
    setNewSearchedRows(filteredRows);
  }

  const handleCancel = () => {
    setSearchValue("");
    setNewSearchedRows(dataCheck);
  }

  const handleFilterStatus = (event) => {
    console.log(event);
    let prop=event.target.value;
    if(prop==="All"){
      setNewSearchedRows(dataCheck);
    }
    else{
      const filteredRows = dataCheck.filter((row) => {
        console.log(row);
        return row.status.includes(prop);
        // || row.txID.includes(prop) 
      });
      console.log(filteredRows);
      setNewSearchedRows(filteredRows);
    }
    
  }
}

export default Filter;
