  //         marginLeft: 45,
  //       //   marginRight:15,
  //         // marginBottom:10,
  //         alignContent:"center",
  //         justifyContent:"center"
  //       }}
  //     >
  //       <h1 className="heading">Wallet Connection</h1>
  //       <Stack spacing={5} direction="column" marginLeft={20} marginTop={10} width={180} alignContent="center" >
  //         <Button variant="contained" onClick={() => {
  //             alert("connected");
  //           }} className="wallet">
  //           Wallet Connection
  //         </Button>
  //         <Button
  //           variant="contained"
  //           onClick={() => {
  //             alert("skipped");
  //           }}
  //         >
  //           Skip
  //         </Button>
  //       </Stack>
  //     </Box>
  //   </>
  // );
// };
export default Wallet;
